import { useState,useEffect } from 'react'
import './App.css';



function App() {
  const [screen, setscreen] = useState(0);
  useEffect( () => {
    setscreen(0)
  },[]);
  return(
    <>
    <div id="output">{screen}</div>
    <div className="buttons"></div>
                <div onclick="backspace()"className="button">&#8592;</div>
                <div onclick="negative()"className="button">+/-</div>
                <div id="erase" onclick="document.getElementById('value').innerHTML=''"className="button">C</div>   
                <div  onclick="calculate()" id="equal" className="button">=</div>  
                <div id="space"></div>  
                <div onclick="show(1)"className="button">1</div>
                <div onclick="show(2)"className="button">2</div>
                <div  onclick="show(3)"className="button">3</div>
                <div  onclick="show('+')"className="button">+</div>
                <div  onclick="show(4)"className="button">4</div>
                <div  onclick="show(5)"className="button">5</div>
                <div  onclick="show(6)"className="button">6</div>
                <div onclick="show('-')"className="button">-</div>
                <div  onclick="show(7)"className="button">7</div>
                <div  onclick="show(8)"className="button">8</div>
                <div  onclick="show(9)"className="button">9</div>
                <div  onclick="show('*')"className="button">*</div>
                <div onclick="show('.')"className="button">.</div>
                <div  onclick="show(0)"className="button">0</div>
                <div id="space2" className="button"></div>   
                <div  onclick="show('/')"className="button">/</div>             
      </>
  );
}

export default App;
