import './App.css'
import ScreenDisplay from './Screen.js'
import Container from './Container'
import BtnContainer from './BtnContainer'
import { useState } from 'react';

const App = () => {
  const [view, setview] = useState("");
  const [Calc, setCalc] = useState("");

const HandleClick = (value) => {
  view = value; 
  ScreenDisplay.value = view;
}

  return(
    <>
      <Container>
        <ScreenDisplay></ScreenDisplay>
        <BtnContainer>
        <div className="buttons" onClick={()=>HandleClick(1)} value='1'>1</div>
        <div className="buttons" value='2'>2</div>
        <div className="buttons" value='3'>3</div>
        <div className="buttons" value='4'>4</div>
        <div className="buttons" value='5'>5</div>
        <div className="buttons" value='6'>6</div>
        <div className="buttons" value='7'>7</div>
        <div className="buttons" value='8'>8</div>
        <div className="buttons" value='9'>9</div>
        <div className="buttons" id="zero" value='0'>0</div>
        <div className="buttons" value='.'>.</div>
        <div className="buttonO" value='-'>-</div>
        <div className="buttonO" value='+'>+</div>
        <div className="buttonO" value='*'>*</div>
        <div className="buttonO" value='/'>/</div>
        <div className="buttonEqual" value='='>=</div>
        <div className="buttonC">+/-</div>
        <div className="buttonC">C</div>
        <div className="buttonC">&#8592;</div>
        </BtnContainer>
      </Container>
    </>
  );
}

export default App;
